mapper.update:
UPDATE nonfacedebatecollect
SET partPers = partPers + 1
WHERE debatecolSeq = #{debateSeq}

mapper.select:
SELECT count(*) FROM nonfacedebate
WHERE debatecolSeq = #{no}
AND userSeq = #{userSeq}

mapper.update:
UPDATE nonfacedebatecollect
SET partPers = partPers - 1
WHERE debatecolSeq = #{debateSeq}

UPDATE nonfacedebate
SET status = 1
WHERE debatecolSeq = #{debateSeq}
AND userSeq = #{userSeq}

UPDATE nonfacedebate
SET status = 0
WHERE debatecolSeq = #{debateSeq}
AND userSeq = #{userSeq}
